import org.apache.commons.io.IOUtils
import java.nio.charset.*
import org.apache.nifi.controller.ControllerService
import groovy.sql.Sql


def lookup = context.controllerServiceLookup
def dbServiceName = databaseConnectionPoolName.value
def dbcpServiceId = lookup.getControllerServiceIdentifiers(ControllerService).find { 
    cs -> lookup.getControllerServiceName(cs) == dbServiceName
}
def conn = lookup.getControllerService(dbcpServiceId)?.getConnection()
try {
flowFile = session.get()
flowFile=session.write(flowFile, {inputStream,outputStream ->

    def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8) 

    def sql = new Sql(conn)

    //def rowsChanged = sql.execute("{call testProcedure2('i92', 'i92')}")
    String s = text.toString()	
    s=s.replaceAll("call spInsUpdateProdAttr", "")
    s=s.replace("('", "")
    s=s.replace("D);", "D")
    s = s.replaceAll("\',","#~#")
    s = s.replaceAll("\'","") 

    String[] str
    str = s.split('#~#')
    def q1 = str[0]
    def q2 = str[1]
    def q3 = str[2]
    def q4 = str[3]


   def rowsChanged = sql.call("{call spInsUpdateProdAttr($q1, $q2,$q3,$q4)}")

    def o = rowsChanged.toString()

    outputStream.write(o.getBytes())

} as StreamCallback)

  flowFile = session.putAttribute(flowFile, 'filename', filename.value)

  session.transfer(flowFile, REL_SUCCESS)

} catch(e) {

    log.error('Scripting error', e)
    session.transfer(flowFile, REL_FAILURE)
}
conn?.close()

import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonOutput
import attributes.transform

flowFile = session.get()
if(!flowFile)return

session.write(flowFile, {inputStream,outputStream->
def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8) 
def o = transform.doImageStore(text.toString())
def output = JsonOutput.toJson(o.toString())
outputStream.write(output.getBytes())
} as StreamCallback)

session.transfer(flowFile, ExecuteScript.REL_SUCCESS)
